'''Created on 2012-9-8@author: zongzong'''from pysnmp.smi import builder, instrum, exval# Importsprint('Loading MIB modules...'),mibBuilder = builder.MibBuilder().loadModules(    'SNMPv2-MIB', 'SNMP-FRAMEWORK-MIB', 'SNMP-COMMUNITY-MIB'    )print('done')print('Building MIB tree...'),mibInstrum = instrum.MibInstrumController(mibBuilder)print('done')print('Remote manager write/create access to MIB instrumentation: '),print(mibInstrum.writeVars(    (((1, 3, 6, 1, 6, 3, 18, 1, 1, 1, 2, 109, 121, 110, 109, 115), 'mycomm'),      ((1, 3, 6, 1, 6, 3, 18, 1, 1, 1, 3, 109, 121, 110, 109, 115), 'mynmsname'),      ((1, 3, 6, 1, 6, 3, 18, 1, 1, 1, 7, 109, 121, 110, 109, 115), 'volatile'))))print('Remote manager read access to MIB instrumentation (table walk)')oid, val = (), Nonewhile 1:    oid, val = mibInstrum.readNextVars(((oid, val),))[0]    if exval.endOfMib.isSameTypeWith(val):        break    print(oid, val.prettyPrint())print('Unloading MIB modules...'),mibBuilder.unloadModules()print('done')(Integer, ObjectIdentifier, OctetString,) = mibBuilder.importSymbols("ASN1", "Integer", "ObjectIdentifier", "OctetString")(NetworkAddress,) = mibBuilder.importSymbols("RFC1155-SMI", "NetworkAddress")(Bits, Counter32, Gauge32, Integer32, Integer32, IpAddress, MibIdentifier, MibScalar, MibTable, MibTableRow, MibTableColumn, TimeTicks, TimeTicks, mgmt,) = mibBuilder.importSymbols("SNMPv2-SMI", "Bits", "Counter32", "Gauge32", "Integer32", "Integer32", "IpAddress", "MibIdentifier", "MibScalar", "MibTable", "MibTableRow", "MibTableColumn", "TimeTicks", "TimeTicks", "mgmt")ifSpeed = MibTableColumn((1, 3, 6, 1, 2, 1, 2, 2, 1, 5), Gauge32()).setMaxAccess("readonly")if mibBuilder.loadTexts: ifSpeed.setDescription("An estimate of the interface's current bandwidth\nin bits per second.  For interfaces which do not\nvary in bandwidth or for those where no accurate\nestimation can be made, this object should contain\nthe nominal bandwidth.")